CREATE TABLE users (`id` INT PRIMARY KEY auto_increment, `public_id` VARCHAR(255), `first_name` VARCHAR(255), `last_name` VARCHAR(255), `email`  VARCHAR(255) NOT NULL UNIQUE, `username` VARCHAR(255) NOT NULL UNIQUE, `pass_hash` VARCHAR(255) NOT NULL, `role` VARCHAR(255) default "user", `connected` BOOL default 0, `active` BOOL default 1, `created_on` timestamp DEFAULT CURRENT_TIMESTAMP, `last_connected` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);

CREATE TABLE jobs (`id` INT PRIMARY KEY auto_increment, `published_by` INT, `order` INT, `public_id` VARCHAR(255), `title` VARCHAR(255),  `location` VARCHAR(255), `type` VARCHAR(255), `time` VARCHAR(255),`salary` VARCHAR(255), `technologies` JSON, `description` TEXT, `active` BOOL DEFAULT 1, `published_on` timestamp DEFAULT CURRENT_TIMESTAMP, `updated_on`TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);